import axios from 'axios';
import EditProfile from './Collection/Modals/EditProfileModal.vue';
export default {
  components: {
    editProfile: EditProfile
  },
  data: function data() {
    return {
      userProfileEditing: {},
      errorObj: {
        isSuccess: true,
        message: ''
      },
      isUniqueEmail: true,
      isShowErrorMessage: false,
      isLoaded: false,
      ROOT_API: process.env.VUE_APP_ROOT_API
    };
  },
  methods: {
    openEditProfileModal: function openEditProfileModal() {
      this.$refs.editProfileModal.show();
    },
    // Only used on edit profile now
    IsEmailUniqueVal: function IsEmailUniqueVal(email) {
      var _this = this;

      this.$store.dispatch('validateEmailAddressUnique', email).then(function (res) {
        _this.isUniqueEmail = res;
      });
    },
    submitEditProfile: function submitEditProfile() {
      var _this2 = this;

      this.isShowErrorMessage = false;
      this.$store.dispatch('editUserProfile', this.userProfileEditing).then(function (data) {
        // edit profile
        if (data.isSuccess) {
          _this2.$refs.editProfileModal.hide();

          _this2.$store.dispatch('user'); // reload user

        } else {
          _this2.errorObj.isSuccess = data.isSuccess;
          _this2.errorObj.message = data.message;
        }
      }).catch(function (err) {// console.log(err)
      }); // Analytics

      this.$ga.event({
        eventCategory: 'Edit_Profile',
        eventAction: 'Submit_Edit_Profile'
      });
    },
    loaded: function loaded() {
      this.isLoaded = true;
    }
  },
  computed: {
    User: function User() {
      var u = this.$store.state.User;
      this.userProfileEditing = u;
      this.userProfileEditing.oldPassword;
      this.userProfileEditing.newPassword;
      this.userProfileEditing.confirmNewPassword;
      return u;
    },
    Collection: function Collection() {
      var length = this.$store.state.Collection.length;

      if (this.$store.state.Collection.length > 0) {
        return length;
      } else return 0;
    },
    watchesFSOT: function watchesFSOT() {
      return this.$store.state.NumberFSOT;
    },
    Favorites: function Favorites() {
      return this.$store.state.Favorites.length;
    }
  },
  created: function created() {
    this.$store.dispatch('getNumberFSOT');
  }
};
