export default {
  name: 'manageCollection',
  props: [],
  data: function data() {
    return {
      conditionToFilterBy: '',
      styleToFilterBy: '',
      statusToFilterBy: '',
      searchTermToFilterBy: '',
      sortCategory: null,
      sortCategories: [{
        value: null,
        text: 'Sort By',
        disabled: true
      }, {
        value: 'condition',
        text: 'Condition'
      }, {
        value: 'status',
        text: 'Status'
      }, {
        value: 'style',
        text: 'Style'
      }, {
        value: 'favorites',
        text: 'Favorites'
      }, {
        value: 'previous',
        text: 'Removed'
      }],
      categoryOptions: [{
        value: null,
        text: 'Sort Criteria',
        disabled: true
      }],
      categoryOption: null
    };
  },
  methods: {
    sizeCardToUse: function sizeCardToUse(val) {
      this.$store.dispatch('sizeCardToUse', val); // Analytics

      this.$ga.event({
        eventCategory: 'Manage_Collection',
        eventAction: 'Size_Watch_Card_Selection',
        eventLabel: "Using (" + val + ") Watch Card Size"
      });
    },
    toggleIsManagingCollection: function toggleIsManagingCollection() {
      this.resetCollectionFilter();
      this.$store.dispatch('toggleIsManagingCollection');
      this.$store.dispatch('toggleIsDragToOrganize', false); // Analytics

      this.$ga.event({
        eventCategory: 'Manage_Collection_Btn',
        eventAction: 'Manage_Collection_Btn_Toggle',
        eventLabel: this.$store.state.isManagingCollection ? 'true' : 'false'
      });
    },
    selectSortCategory: function selectSortCategory(eventLabel) {
      this.searchTermToFilterBy = '';
      this.sortCategory = eventLabel;
      this.categoryOption = null;

      switch (eventLabel) {
        case 'condition':
          this.categoryOptions = [{
            value: null,
            text: 'Category',
            disabled: true
          }, {
            value: 8,
            text: '8-10'
          }, {
            value: 5,
            text: '5-7'
          }, {
            value: 1,
            text: '0-4'
          }];
          break;

        case 'status':
          this.categoryOptions = [{
            value: null,
            text: 'Category',
            disabled: true
          }, {
            value: 'keeper',
            text: 'Keeper'
          }, {
            value: 'sale',
            text: 'For Sale'
          }, {
            value: 'trade',
            text: 'For Trade'
          }, {
            value: 'fsot',
            text: 'Sale / Trade'
          }];
          break;

        case 'style':
          this.categoryOptions = [{
            value: null,
            text: 'Category',
            disabled: true
          }, {
            value: 'diver',
            text: 'Diver'
          }, {
            value: 'dress',
            text: 'Dress'
          }, {
            value: 'chronograph',
            text: 'Chronograph'
          }, {
            value: 'tourbillon',
            text: 'Tourbillon'
          }, {
            value: 'black tie',
            text: 'Black Tie'
          }, {
            value: 'sport',
            text: 'Sport'
          }, {
            value: 'smart',
            text: 'Smart'
          }, {
            value: 'apple',
            text: 'Apple'
          }, {
            value: 'casual',
            text: 'Casual'
          }, {
            value: 'orienteering',
            text: 'Orienteering'
          }];
          break;

        case 'favorites':
          this.selectCategoryOption('all');
          this.categoryOptions = [{
            value: 'all',
            text: 'All Favorites' // { value: 'inCollection', text: 'Favorites in Collection'},
            // { value: 'notInCollection', text: 'Favorites not in Collection'},
            // { value: 'pastFavorites', text: 'Previous Favorites'},

          }];
          break;

        case 'previous':
          //   this.searchTermToFilterBy = null
          this.selectCategoryOption('all');
          this.categoryOptions = [{
            value: 'all',
            text: 'All Previous'
          }];
          break;

        default:
          this.sortCategory = null;
          this.categoryOption = null;
          this.categoryOptions = [{
            value: null,
            text: 'Select Options',
            disabled: true
          }];
      }
    },
    selectCategoryOption: function selectCategoryOption(eventLabel) {
      this.categoryOption = eventLabel;
      this.filterCollection(this.sortCategory, this.categoryOption);
    },
    filterCollection: function filterCollection(sortCategory, categoryOption) {
      var filterObj = {
        category: sortCategory,
        option: categoryOption //   this.$store.dispatch('isTryingShuffleWhileManage', false) 

      };
      this.$store.dispatch('getFilteredCollection', filterObj);

      if (sortCategory != 'previous') {
        this.$store.dispatch('viewingPreviousWatches', false);
      } // Analytics


      this.$ga.event({
        eventCategory: 'Manage_Collection',
        eventAction: 'Search_By_Filters',
        eventLabel: "Searching by filters: " + sortCategory + ' - ' + categoryOption
      });
    },
    filterBySearchTerm: function filterBySearchTerm(eventLabel) {
      if (eventLabel) {
        // this.$store.dispatch('isTryingShuffleWhileManage', false) 
        this.$store.dispatch('getFilteredCollectionBySearchTerm', eventLabel.toLowerCase());
      } else {
        this.$store.dispatch('viewingPreviousWatches', false);
        this.sortCategory = null;
        this.categoryOption = null;
        this.$store.dispatch('getFilteredCollectionBySearchTerm', '');
      } // Analytics


      this.$ga.event({
        eventCategory: 'Manage_Collection',
        eventAction: 'Search_By_Term',
        eventLabel: "Searching by term: " + eventLabel
      });
    },
    selectRandomWatch: function selectRandomWatch() {
      this.$emit('selectRandomWatch');
    },
    addNewWatch: function addNewWatch() {
      this.$emit('addNewWatch');
      this.isAddingWatch = true;
      this.isEditingExistingWatch = false;
    },
    resetCollectionFilter: function resetCollectionFilter() {
      this.selectSortCategory();
      this.$store.dispatch('viewingPreviousWatches', false);
      this.$store.state.FilteredCollection = this.$store.state.Collection;
    }
  },
  computed: {
    isManagingCollection: {
      get: function get() {
        return this.$store.state.isManagingCollection;
      },
      set: function set(value) {
        this.$store.dispatch('toggleIsManagingCollection');
      }
    },
    isShowFlags: {
      get: function get() {
        return this.$store.state.isShowFlags;
      },
      set: function set(value) {
        this.$store.dispatch('toggleIsShowFlags');
      }
    },
    isShowEditFlags: {
      get: function get() {
        return this.$store.state.isShowEditFlags;
      },
      set: function set(value) {
        this.$store.dispatch('toggleIsShowEditFlags', value);
      }
    },
    isDragToOrganize: {
      get: function get() {
        return this.$store.state.isDragToOrganize;
      },
      set: function set(value) {
        this.$store.dispatch('toggleIsDragToOrganize', value);
      }
    },
    currentCardSize: function currentCardSize() {
      return this.$store.state.CurrentCardSize;
    }
  }
};
