    closeAutCompleteBoxes: function closeAutCompleteBoxes() {
      this.isShowBrandAutoCompleteBox = false;
      this.isShowMovementAutoCompleteBox = false;
      this.autoCompleteResults = [];
    },
    onArrowDown: function onArrowDown() {
      if (this.arrowCounter < this.autoCompleteResults.length) {
        this.arrowCounter = this.arrowCounter + 1;
      }
    },
    onArrowUp: function onArrowUp() {
      if (this.arrowCounter > 0) {
        this.arrowCounter = this.arrowCounter - 1;
      }
    },
    onEnter: function onEnter() {
      this.selectAutoCompleteResult(this.keyboardEnterSubmitAddWatchFieldToSet, this.autoCompleteResults[this.arrowCounter]);
      this.arrowCounter = -1;
    },
    autoCompleteSearchForBrands: function autoCompleteSearchForBrands(searchTerm, addWatchField, Category) {
      // searchTerm is set with v-model
      this.isShowBrandAutoCompleteBox = true;
      this.keyboardEnterSubmitAddWatchFieldToSet = "brand"; //get all amatching

      var allMatchingResults = AutoComplete.AutoComplete(Category, searchTerm);
      var StartingLetterMatchesSearchTerm = allMatchingResults.filter(function (result) {
        return result.charAt(0).toLowerCase() == searchTerm.charAt(0).toLowerCase();
      }); // concat, remove matching

      var catArr = StartingLetterMatchesSearchTerm.concat(allMatchingResults);
      this.autoCompleteResults = catArr.filter(function (result, index) {
        return catArr.indexOf(result) == index;
      });
    },
    autoCompleteSearchForMovements: function autoCompleteSearchForMovements(searchTerm, addWatchField, Category) {
      // searchTerm is set with v-model
      this.isShowMovementAutoCompleteBox = true;
      this.keyboardEnterSubmitAddWatchFieldToSet = "movement"; //get all amatching

      var allMatchingResults = AutoComplete.AutoComplete(Category, searchTerm);
      var StartingLetterMatchesSearchTerm = allMatchingResults.filter(function (result) {
        return result.charAt(0).toLowerCase() == searchTerm.charAt(0).toLowerCase();
      }); // concat, remove matching

      var catArr = StartingLetterMatchesSearchTerm.concat(allMatchingResults);
      this.autoCompleteResults = catArr.filter(function (result, index) {
        return catArr.indexOf(result) == index;
      });
    },
    selectAutoCompleteResult: function selectAutoCompleteResult(fieldToSet, result) {
      var _this = this;

      this.addWatch[fieldToSet] = result;
      setTimeout(function () {
        _this.isShowBrandAutoCompleteBox = false;
        _this.isShowMovementAutoCompleteBox = false;
        _this.autoCompleteResults;
      });
    },
