      var options = {
        format: 'jpeg',
        circle: false // this.isShowingCroppie = false

      };
      this.$refs.croppieRef.result(options);
    },
    SetCropBackToImageSrc: function SetCropBackToImageSrc(croppedImg) {
      var croppieIndex = this.$store.state.croppieWatchOrder;
      this.addWatch.src.images[croppieIndex].src = croppedImg;
    },
    result: function result(output) {
      this.cropped = output;
    },
    update: function update(val) {
      // this.$refs.croppieRef.update(val);
      this.crop();
    },
    rotate: function rotate() {
      // Rotates the image
      this.$refs.croppieRef.rotate(-90); // this.crop()

      this.$ga.event({
        eventCategory: 'Manage_Collection',
        eventAction: 'Rotate_Watch_Btn_Click',
        eventLabel: 'Rotating Crop Watch Image'
      });
    },
    addWatchSpecs: function addWatchSpecs(specs) {
      this.addWatch.specs = localStorage.setItem('addWatch', specs);
    },
    checkValidInput: function checkValidInput(val) {
      if (val < 0) {
        this.addWatch.condition = 0;
      } else if (val > 10) {
        this.addWatch.condition = 10;
      } else {
        this.addWatch.condition = val;
      }
    },
    setImagesOnAddWatch: function setImagesOnAddWatch(imagesObjArray) {
      var _this3 = this;

      imagesObjArray.forEach(function (fileB64Obj) {
        var imageObjToPush = {
          src: fileB64Obj.src,
          fileName: fileB64Obj.fileName,
          // fileExtension: fileB64Obj.fileExtension,
          order: _this3.addWatch.src.images.length // s3 bucket url path

        };

        _this3.addWatch.src.images.push(imageObjToPush);
      }); // console.log('loupiie', imgArr)
    },
