export default {
  name: "onScrollMenu",
  data: function data() {
    return {
      isScrolling: false,
      needsMarginBottom: false,
      isOnProfilePage: false
    };
  },
  methods: {
    scrollToTop: function scrollToTop() {
      window.scrollTo(0, 0);
    },
    handleScroll: function handleScroll() {
      var scrollTop = window.scrollY; // current position in document (px)

      var windowHeight = window.innerHeight; // user's visible screen remaing (px)

      var documentHeight = document.body.clientHeight; // total document height (px)

      this.isScrolling = window.scrollY > 175;
      console.log(documentHeight);

      if (scrollTop + windowHeight > documentHeight + 735) {
        console.log(scrollTop + windowHeight, documentHeight + 775);
        this.needsMarginBottom = true;
      } else {
        this.needsMarginBottom = false;
      }
    }
  },
  computed: {
    isDragToOrganize: {
      get: function get() {
        return this.$store.state.isDragToOrganize;
      },
      set: function set(value) {
        this.$store.dispatch('toggleIsDragToOrganize', value);
      }
    },
    IsCollectionLength: function IsCollectionLength() {
      return this.$store.state.Collection.length > 0;
    },
    IsManagingCollection: function IsManagingCollection() {
      return this.$store.state.isManagingCollection;
    }
  },
  created: function created() {
    window.addEventListener('scroll', this.handleScroll);

    if (window.location.pathname == '/profile') {
      this.isOnProfilePage = true;
    }

    this.isOnProfilePage = false;
  },
  destroyed: function destroyed() {
    window.removeEventListener('scroll', this.handleScroll);
  }
};
