import axios from 'axios';
export default {
  name: 'register',
  data: function data() {
    return {
      ROOT_API: process.env.VUE_APP_ROOT_API,
      form: {
        email: '',
        firstName: '',
        lastName: '',
        password: ''
      },
      card: 1,
      showAlert: false,
      responseMessage: '',
      responseStyle: 'light',
      isRefreshPage: false,
      showForm: true,
      passwordErrMsg: '',
      formValid: true
    };
  },
  methods: {
    onSubmit: function onSubmit() {
      var _this = this;

      this.showAlert = false;
      this.form.email.toLowerCase();
      this.$store.dispatch('register', this.form).then(function (res) {
        if (res.isSuccess) {
          _this.$store.dispatch('sendWelcomeEmail', _this.form);

          _this.$router.push({
            path: '/profile'
          });
        } else {
          _this.showAlert = true;
          _this.responseMessage = res.message;
          _this.responseStyle = 'danger';
          _this.isRefreshPage = res.isRefreshPage;

          _this.$emit('ToggleShowingAlert', true);
        }
      }).catch(function (err) {
        _this.showAlert = true;
        _this.responseMessage = err.message;
        _this.responseStyle = 'danger';

        _this.$emit('ToggleShowingAlert', true);
      });
    },
    toggleAuthChild: function toggleAuthChild() {
      this.$emit('toggleAuthView');
    },
    validateEmail: function validateEmail(email) {
      // regex expression making sure email is in valid format
      var re = /^(([^<>()\[\]\\.,;:\s@"]+(\.[^<>()\[\]\\.,;:\s@"]+)*)|(".+"))@((\[[0-9]{1,3}\.[0-9]{1,3}\.[0-9]{1,3}\.[0-9]{1,3}\])|(([a-zA-Z\-0-9]+\.)+[a-zA-Z]{2,}))$/;

      if (re.test(email)) {
        return true;
      } else {
        this.form.password = '';
        return re.test(email);
      }
    },
    validatePassword: function validatePassword(password) {
      if (password.length < 4) {
        this.passwordErrMsg = 'Password must be at least 4 characters';
        return false;
      } else {
        this.passwordErrMsg = '';
        return true;
      }
    },
    validateNames: function validateNames(first, last) {
      if (first.length && last.length) return true;else return false;
    },
    reloadPage: function reloadPage() {
      location.reload();
    } // isDuplicateEmail(email)
