import axios from 'axios';
import EditProfile from './Collection/Modals/EditProfileModal.vue';
export default {
  components: {
    editProfile: EditProfile
  },
  data: function data() {
    return {
      userProfileEditing: {},
      errorObj: {
        isSuccess: true,
        message: ''
      },
      isShowErrorMessage: false,
      isLoaded: false,
      ROOT_API: process.env.VUE_APP_ROOT_API
    };
  },
  methods: {
    submitEditProfile: function submitEditProfile() {
      var _this = this;

      this.isShowErrorMessage = false;
      this.$store.dispatch('editUserProfile', this.userProfileEditing).then(function (data) {
        // edit profile
        if (data.isSuccess) {
          _this.$refs.editProfileModal.hide();

          _this.$store.dispatch('user'); // reload user

        } else {
          _this.errorObj.isSuccess = data.isSuccess;
          _this.errorObj.message = data.message;
        }
      }).catch(function (err) {
        console.log(err);
      });
    },
    loaded: function loaded() {
      this.isLoaded = true;
    }
  },
  computed: {
    User: function User() {
      var u = this.$store.state.User;
      this.userProfileEditing = u;
      this.userProfileEditing.oldPassword;
      this.userProfileEditing.newPassword;
      this.userProfileEditing.confirmNewPassword;
      return u;
    },
    Collection: function Collection() {
      var length = this.$store.state.Collection.length;

      if (this.$store.state.Collection.length > 0) {
        return length;
      } else return 0;
    },
    watchesFSOT: function watchesFSOT() {
      return this.$store.state.NumberFSOT;
    },
    Favorites: function Favorites() {
      return this.$store.state.Favorites.length;
    } // errorObj() {
    //     return {
    //         isSuccess: true,
    //         message: 'Test'
    //     }
    // }

  },
  created: function created() {
    this.$store.dispatch('getNumberFSOT');
  }
};
