export default {
  name: 'fileSelector',
  props: {
    value: File,
    isPreviewBox: Boolean
  },
  data: function data() {
    return {
      ROOT_API: process.env.VUE_APP_ROOT_API,
      isS3Uploading: false,
      imgBase64Array: []
    };
  },
  methods: {
    uploadImagesToBase64: function uploadImagesToBase64(e) {
      var files = e.target.files;
      this.TransformFilesIntoBase64Array(files);
    },
    TransformFilesIntoBase64Array: function TransformFilesIntoBase64Array(files) {
      var vm = this;

      var _loop = function _loop(index) {
        var fSize = files[index].size / 1024 / 1024;

        if (fSize < 3) {
          // 3MB max file size
          var FR = new FileReader();

          FR.onload = function (e) {
            var result = e.target.result;
            var imgBase64Url = e.target.result;
            vm.imgBase64Array.push({
              imgBase64Url: imgBase64Url,
              order: index,
              fileName: files[index].name
            });

            if (vm.imgBase64Array.length == files.length) {
              vm.$emit('setImagesOnAddWatch', vm.imgBase64Array);
            }
          };

          FR.readAsDataURL(files[index]);
        } else {
          console.log('File size is too large', fSize, '/ 3');
        }
      };

      for (var index = 0; index < files.length; index++) {
        _loop(index);
      }
    }
  }
};
