import axios from 'axios'

export default {
  name: 'seeMoreModal',

  props: ['selectedWatch', 'isEdit'],

  data () {
    return {
      isUsersWatch: false,
      isShowDetails: false
    }
  },
  methods: {
  toggleShowDetails() {
      this.isShowDetails = !this.isShowDetails

      this.$ga.event({
        eventCategory: 'See_More_Modal',
        eventAction: 'Show/Hide_Details_Toggle',
        eventLabel: this.isShowDetails
    })
  },

    watchStyleDisplayName (style) {
      return style.charAt(0).toUpperCase() + style.substr(1)
    },

    titleCase (str) {
      var splitStr = str.toLowerCase().split(' ')
      for (var i = 0; i < splitStr.length; i++) {
        splitStr[i] = splitStr[i].charAt(0).toUpperCase() + splitStr[i].substring(1)
      }
      return splitStr.join(' ')
    },

    CapitalizeFirstLetter (string) {
      let lower = string
      return lower.replace(/^\w/, c => c.toUpperCase())
    }
  },

  computed: {
    CapitalizedMovementType () {
      return this.CapitalizeFirstLetter(this.selectedWatch.movementType)
    },

    CapitalizeCrystal () {
      return this.CapitalizeFirstLetter(this.selectedWatch.crystal)
    }
  }
}
