result:
    .no-pointer {
        pointer-events: none;
    }

    #watchName {
        font-size: .80em;
    }

    #seeMore {
        font-size: .70em;
    }

    #searchRef {
        font-size: .60em;
    }

    .btn {
        box-shadow: 0 3px 6px rgba(0,0,0,0.16), 0 3px 6px rgba(0,0,0,0.23);
    }

    .right {
        float: right;
    }

    .left {
        float: left;
    }

    .flag {
        padding: .35em 0 !important;
        font-size: .75em;
    }

    .p-half {
        padding: .5rem;
    }

    .border-xl {
        border: 1px solid lightgray;
    }

    .watchImgWrapper_Sm {
        width: 100%;
        height: 8rem;
    }

    .watchImgWrapper_Md {
        width: 100%;
        height:11.5rem;
    }

    .watchImgWrapper_Lg {
        width: 100%;
        height: 17.5rem;
    }

    @media(max-width: 850px) {
        .watchImgWrapper_Sm {
            width: 100%;
            height: 3rem;
        }

        .watchImgWrapper_Md {
            width: 100%;
            height:4rem;
        }

        .watchImgWrapper_Lg {
            width: 100%;
            height: 6rem;
        }
    }

    .watchImg {
        width: auto;
        max-width: 100%;
        min-height: 75%;
        height: auto;
        margin:auto;
        max-height: 100%;
    }

    .tallWatchImg {
        width: auto;
        /* max-height: 25rem; */
    }

    .smallHeartIcon {
        width: 25px;
        z-index: 999;
    }

    .heartIcon {
        width: 42.5px;
        z-index: 999;
    }

    .swap-list-move {
        transition: transform .7s;
    }

    @media(max-width: 1000px) {
        .border-xl{
            border: none;
        }

        /* .watchImg {
            width: 100%;
            min-height: 6rem;
            object-fit: cover;

        } */
    }

    @media(min-width: 768px) and (max-width: 1000px) {
        #searchRef, #seeMore {
            font-size: .6em;
        }

        #watchName {
            font-size: .65em;
        }

        .heartIcon {
            width: 20px;
        }
    }

    @media(max-width: 600px) {
        #editIcon {
        background-size: 1.25em;
        }

        .flag {
            font-size: .85rem;
            padding: .1em !important;

        }

        .heartIcon {
            width: 28px;
        }

        .smallHeartIcon {
        width: 15px;
        z-index: 999;
    }
    }

    @media(max-width: 415px) {
        #searchRef, #seeMore {
            font-size: .65rem;
        }

        .flag {
            font-size: 10.5px;
            padding: .2em !important;
        }

        .heartIcon {
            width: 22.5px;
        }
    }

