import axios from 'axios'
export default {
  data () {
    return {
      form:
            {
              email: '',
              password: ''
            },
      showAlert: false,
      responseMessage: 'Login Here!',
      responseStyle: 'light'
    }
  },
  methods: {
    onSubmit () {
      this.showAlert = false
      this.$emit('toggleAuthLoading', true)
      this.$store.dispatch('login', this.form).then((res) => {
        this.$emit('toggleAuthLoading', false)
        if (res.isSuccess) {
          this.$router.push({ path: '/profile' })
          this.showAlert = false
        } else {
          this.showAlert = true
          this.responseStyle = 'danger'
          this.$emit('ToggleShowingAlert', true)          
          this.responseMessage = res.message
        }
      })
    },

    toggleAuthChild () {
      this.$emit('ToggleShowingAlert', false)  
      this.$emit('toggleAuthView')
    },

    toggleAuthLoading () {
      this.$emit(toggleAuthLoading, true)
    }
  }
}
