export default {
  methods: {
    logout () {
      this.$store.dispatch('logout').then(() => {
        location.reload()
      })
    },

    tryToggle () {
      if (window.innerWidth < 993) {
        this.$refs.navCollapse.toggle()
      }
    }
  },

  computed: {
    isAuthorized () {
      return this.$store.state.isAuthorized
    }
  }
}
